import React, { Component } from 'react'
import { Carousel } from "react-responsive-carousel"
import "react-responsive-carousel/lib/styles/carousel.min.css"

export default class Game extends Component {

  renderText = () => {
    const { game } = this.props
    const { texts } = game
    const { toggleShowGame } = this.props

    let textToRender = texts.map((textObj, i) => {
      return (
        <div key={i} className='slideContainer'>
        <button onClick={() => toggleShowGame()}>Tillbaka</button>

          <div className='slide'>
            {textObj}
          </div>
        </div>
      )
    })

    return textToRender
  }

  render() {

    return (
      <div>
        <Carousel showThumbs={false} transitionTime={200} showStatus={false}>
          {this.renderText()}
        </Carousel>
      </div>
    )
  }
}
